CREATE TABLE IF NOT EXISTS "__EFMigrationsHistory" (
    "MigrationId" character varying(150) NOT NULL,
    "ProductVersion" character varying(32) NOT NULL,
    CONSTRAINT "PK___EFMigrationsHistory" PRIMARY KEY ("MigrationId")
);

CREATE TABLE "Categoria" (
    "Id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "Nombre" character varying NOT NULL,
    CONSTRAINT "PK_Categoria" PRIMARY KEY ("Id")
);

CREATE TABLE "Producto" (
    "Id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "Nombre" character varying(300) NOT NULL,
    "Precio" numeric(18,2) NOT NULL,
    "PrecioPromocional" numeric(18,2) NULL,
    "Codigo" character varying(300) NULL,
    "Costo" numeric(18,2) NULL,
    "Unidad" character varying(50) NULL,
    "VerCatalogo" boolean NULL,
    "IdCategoria" integer NULL,
    CONSTRAINT "PK_Producto" PRIMARY KEY ("Id"),
    CONSTRAINT "Fk_Producto_Categoria" FOREIGN KEY ("IdCategoria") REFERENCES "Categoria" ("Id") ON DELETE RESTRICT
);

CREATE UNIQUE INDEX "UQ_Categoria_Nombre" ON "Categoria" ("Nombre");

CREATE INDEX "IX_Producto_IdCategoria" ON "Producto" ("IdCategoria");

